package me.shetj.base.pay.alipay;import java.util.Map;/** * @author admin */public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			switch (key) {				case "resultStatus":					resultStatus = rawResult.get(key);					break;				case "result":					result = rawResult.get(key);					break;				case "memo":					memo = rawResult.get(key);					break;				default:					break;			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo						+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}